package loops;

public class WAP10to1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=10;i>=1;i--) {
			System.out.println(i);
		}
		System.out.println("===========");
		int i=10;
		while(i>=1) {
			System.out.println(i);
			i--;
		}
		System.out.println("===========");
		int k=10;
		do {
			System.out.println(k);
			k--;
		}while(k>=1);

	}

}
